const mongoose = require('mongoose');
const Schema = mongoose.Schema;
const TimestampsPlugin = require('mongoose-timestamps');
const FindOrCreatePlugin = require('mongoose-findorcreate');

const ProfileSchema = Schema({
    title: {
        type: String,
        required: true
    },
    __v: {
        type: Number,
        select: false
    }
});

ProfileSchema.set('toJSON', {
    transform: (doc, result, options) => {
        result.id = result._id;
        delete result._id;
        return result;
    }
});


ProfileSchema.plugin(Utils.URIPlugin, {path: '/profiles'});
ProfileSchema.plugin(TimestampsPlugin);
ProfileSchema.plugin(FindOrCreatePlugin);

module.exports = mongoose.model('Profile', ProfileSchema);