module.exports = app => {
    const router = app.Router();

    /**
     * @api {get} /profiles    Request list of profiles
     * @apiName List
     * @apiGroup Profile
     * @apiVersion 1.0.0
     */
    router
        .get('/',
            app.actions.profiles.list.expose()
        );

    app.server.use(`${app.settings.basePath}/v${app.settings.version}/profiles`, router);
};