module.exports = app => {
    const Casting = app.models.Casting;

    return Action({
        execute: (context) => {
            return Casting
                .findByIdAndUpdate(context.params.id, context.data)
                .then(Utils.ensureOne({code: 404, reason: 'casting.not.found'}))
                .then(context.noContent)
                .catch(error => Promise.reject(error))
        }
    });
};
