module.exports = app => {
    const Concrete = app.models.Concrete;

    return Action({
        execute: (context) => {
            return Concrete
                .findByIdAndUpdate(context.params.id, context.data)
                .then(Utils.ensureOne({code: 404, reason: 'concrete.not.found'}))
                .then(context.noContent)
                .catch(error => Promise.reject(error))
        }
    });
};
