module.exports = app => {
    const Project = app.models.Project;

    return Action({
        execute: (context) => {
            return Project
                .findByIdAndUpdate(context.params.id, context.data)
                .then(Utils.ensureOne({code: 404, reason: 'project.not.found'}))
                .then(context.noContent);
        }
    });
};
