module.exports = app => {
    const Profile = app.models.Profile;

    return Action({
        execute: (context) => {
            return Profile
                .find(context.criteria.where)
                .skip(context.criteria.skip)
                .limit(context.criteria.limit)
                .sort(context.criteria.sort)
                .populate(context.criteria.includes);
        }
    });
};
