module.exports = app => {
    const Project = app.models.Project;

    return Action({
        execute: (context) => {
            return Project
                .findById(context.params.id)
                .select(context.criteria.fields)
                .skip(context.criteria.skip)
                .limit(context.criteria.limit)
                .sort(context.criteria.sort)
                .populate(context.criteria.includes)
                .then(Utils.ensureOne.notFound('project.not.found'));
        }
    });
};
