global.Action   = require('../index').Action;
global.Utils    = require('./utils');

const Core = require('../index').Core;
const app  = new Core();

app
    .on(Core.events.started, app => console.log(`Server listening on port ${app.settings.port}`))
    .on(Core.events.started,              require('./docs'))
    .on(Core.events.started,              require('./static'))
    .start();

// Exposing the app allowing tests.
module.exports = app;
